
public class Multiple_parameters {

	public static void myMethod(String fname, int age){
		// TODO Auto-generated method stub
		System.out.println(fname + " is " + age);
		
	}
	public static void main(String args[]) {
		myMethod("Lion", 25);
		myMethod("Tiger", 20);
		myMethod("Cheethah", 15);
	}
}
