
public class Arithmatic_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		int y = 3;
		int s = 5;
		++x;
		int z = 4;
		--z;
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
		System.out.println(x);
		System.out.println(z);
		
	}

}
