
public class Assignment_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		int a = 5;
		a += 3;
		int b = 5;
		b -= 3;
		int c = 5;
		c *= 3;
		int d = 5;
		d /= 3;
		System.out.println(x);
		System.out.println(a);
		System.out.println(b);
		System.out.println(c);
		System.out.println(d);
		//%=, &=, |=, ^=, >>=, <<=
	}

}
