
public class Comparison_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		int y = 3;
		System.out.println(x == y); //returns false because of 5 is not equals to 3
		System.out.println(x != y); //returns true because of 5 is not equals to 3
		System.out.println(x > y); //returns true because of 5 is greater than 3
		System.out.println(x < y); //returns false because of 5 is less than 3
		System.out.println(x >= y); //returns true because of 5 is greater, or equal to 3
		System.out.println(x <= y); //returns false because of 5 is neither less than or equal to 3
		
	}

}
