
public class Special_char_single_quote {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String txt = "We are the so called \"Vikkings\" from the north."; //Doble quote
		String txt1 = "It\'s alright."; //Single quote
		String txt2 = "The character \\ is called backlash.";//Backlash
		String txt3 = "Hello\nWorld!";
		String txt4 = "Hello\rWorld!";
		String txt5 = "Hello\tWorld!";
		String txt6 = "Hel\blo World";
		String txt7 = "Hello\fWorld";
		System.out.println(txt);
		System.out.println(txt1);
		System.out.println(txt2);
		System.out.println(txt3);
		System.out.println(txt4);
		System.out.println(txt5);
		System.out.println(txt6);
		System.out.println(txt7);
	}

}
